const AvailableMeals=()=>{
    const fetchmeals=useCallback(async()=>{
      const response=await fetch('https://react-http-1df71-default-rtdb.firebaseio.com/meals.json');
      if(!response.ok){
        throw new Error('Error occured!');
      }
      const data=await response.json();
      console.log(data);
      const meals=[];
      for(const meal in data){
        meals.push(
          {
            id:key,
            name:meal[key].name,
            description:meal[key].description,
            price:meal[key].price,
          }
        )
      }
      return meals;
    },[]);

    const DUMMY_MEALS=fetchmeals();
    const mealsList = DUMMY_MEALS.map((meal) =>(<MealItem id={meal.id} key={meal.id} name={meal.name} description={meal.description} price={meal.price}/>));

    return (
      <section className={classes.meals}>
        <Card>
          <ul>{mealsList}</ul>
        </Card>
      </section>
    );
};
export default AvailableMeals;
